import styled from 'styled-components';
import { media } from '../../styles/utils';


export const Title = styled.h3`
  margin-bottom: 2rem;
`;

export const Films = styled.div`
  display: flex;
  flex-direction: column;

  ${media.tablet`
    flex-direction: row;
  `}

`;
