import React from 'react';
import fetch from 'isomorphic-unfetch';

const Planet = ({ data }) => {
  const { Name, Climate, Population, Films } = data;

  return (
    <div>Planet</div>
  );
}

Planet.getInitialProps = async ({ res, query }) => {
  try {
    const response = await fetch(`http://localhost:3000/api/planets/${query.stub}`);
    const data = await response.json();

    if (Object.entries(data).length === 0) {
      res.statusCode = 404;
      return { data: null, statusCode: 404 };
    }

    res.statusCode = 200;
    return { data };
  } catch (err) {
    res.statusCode = 500;
    return {
      err,
      statusCode: 500,
      message: 'Server error'
    }
  }
};

export default Planet;
