import styled from 'styled-components';
import { media } from '../../styles/utils';

import { Container as TextLabelContainer } from '../UI/TextLabel/TextLabelStyles';


export const Title = styled.h3`
  margin-bottom: 2rem;
`;

export const Films = styled.div`
  display: flex;
  flex-direction: column;
`;

export const Film = styled.div`
  display: flex;
  justify-content: center;
  flex-direction: row;
  margin-bottom: 2rem;

  ${TextLabelContainer} {
    :first-child {
      flex-grow: 2;
    }

    :last-child {
      margin-left: 2rem;
    }
  }
`;
