import styled from 'styled-components';
import { media } from '../styles/utils';

export const ButtonLink = styled.a`
  padding: 1rem 2rem;
  text-align: center;
  color: ${props => props.theme.color.white};
  font-weight: old;
  border-radius: .3rem;
  background-color: ${props => props.theme.color.blue};
  display: inline-block;
`;
