import styled from 'styled-components';

export const Container = styled.div`
  display: flex;
  text-align: center;
  flex-direction: column;
  align-items: center;
  background-color: ${props => props.theme.color.white};
  padding: 4rem;

  h1 {
    font-size: 4rem;
    margin-bottom: 2rem;
  }

  h4 {
    font-size: 2rem;
    margin-bottom: 4rem;
  }
`;
